INSERT INTO `wp_posts` VALUES(631, 104, '2011-09-10 14:58:05', '2011-09-10 11:58:05', '<p style="text-align: justify;">Pagaliau startuoja ne tik mūsų internetinis puslapis, tačiau ir konkursas "Pagauk sprotininką". Taip, tai iššūkis ne kiekvieno nosiai, bet gal tu greitesnis už juos?? Tau reikia tik dviejų dalykų - fotografuojančio daikto ir velniškai didelės drąsos. Kuo netikėtesnė vieta, kuo spalvingesnis sportinis kostiumas, kuo arčiau prisėlinsi - tuo didesnė tikimybė laimėti puikų prizą, kurį išvysite jau labai greitai. "Numeskite" mums nuotraukas facebook''e ant sienos arba siųskite el paštu. Įrodyk, kad tu kietesnis už sportininką!<!--more--></p>\r\n<p style="text-align: justify;">P.S. Laukiamos nuotraukos ir su sportininkų palydovėmis - rožiniai batukai, visa kita. :)</p>\r\n<p style="text-align: justify;">[nggallery id=4]</p>', 'Pagauk sportininką!', '', 'publish', 'open', 'open', '', 'pagauk-sportininka-2', '', '', '2011-09-14 16:07:06', '2011-09-14 13:07:06', '', 0, 'http://3juosteles.lt/?p=631', 0, 'post', '', 0);
