INSERT INTO `wp_posts` VALUES(699, 104, '2011-09-14 15:47:28', '2011-09-14 12:47:28', '<p style="text-align: justify;">Pagaliau startuoja ne tik mūsų internetinis puslapis, tačiau ir konkursas "Pagauk sprotininką". Taip, tai iššūkis ne kiekvieno nosiai, bet gal tu greitesnis už juos?? Tau reikia tik dviejų dalykų - fotografuojančio daikto ir velniškai didelės drąsos. Kuo netikėtesnė vieta, kuo spalvingesnis sportinis kostiumas, kuo arčiau prisėlinsi - tuo didesnė tikimybė laimėti puikų prizą, kurį išvysite jau labai greitai. "Numeskite" mums nuotraukas facebook''e ant sienos arba siųskite el paštu. Įrodyk, kad tu kietesnis už sportininką!<!--more--></p>\r\n<p style="text-align: justify;">P.S. Laukiamos nuotraukos ir su sportininkų palydovėmis - rožiniai batukai, visa kita. :)</p>', 'Pagauk sportininką!', '', 'inherit', 'open', 'open', '', '631-revision-3', '', '', '2011-09-14 15:47:28', '2011-09-14 12:47:28', '', 631, 'http://3juosteles.lt/?p=699', 0, 'revision', '', 0);
