 aš!', '', 'inherit', 'open', 'open', '', '907-revision-7', '', '', '2011-09-22 17:20:59', '2011-09-22 14:20:59', '', 907, 'http://3juosteles.lt/?p=916', 0, 'revision', '', 0);
